import "dotenv/config";
import * as fs from "fs";
import * as path from "path";
import { fileURLToPath } from "url";
import { PrismaBetterSqlite3 } from "@prisma/adapter-better-sqlite3";
import { PrismaClient } from "../generated/prisma/client.js";
import {
  scrapeZaraProducts,
  clearProductsUploads,
  Product,
} from "../src/lib/zara-scraper.js";

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);
const rootDir = path.join(__dirname, "..");

const connectionString = `${process.env.DATABASE_URL}`;
const adapter = new PrismaBetterSqlite3({ url: connectionString });
const prisma = new PrismaClient({ adapter });

async function seed() {
  console.log("üå± Starting seed process...\n");

  const uploadsDir = path.join(rootDir, "uploads", "products");

  // Step 1: Clear uploads/products directory
  console.log("üìÅ Step 1: Clearing uploads/products directory...");
  clearProductsUploads(uploadsDir);

  // Step 2: Clear Product table
  console.log("\nüóëÔ∏è  Step 2: Clearing Product table...");
  const deletedCount = await prisma.product.deleteMany();
  console.log(`Deleted ${deletedCount.count} products from database`);

  // Step 3: Run scraping
  console.log("\nüï∑Ô∏è  Step 3: Running Zara scraper...");

  // Sample HTML content - replace this with actual HTML or fetch from URL
  // For production, you might want to fetch this from Zara's website
  const htmlContent = getZaraHtmlContent();

  const products = await scrapeZaraProducts(
    htmlContent,
    true,
    "masculino",
    uploadsDir,
  );

  console.log(`\nüì¶ Scraped ${products.length} products`);

  // Step 4: Insert products into database
  console.log("\nüíæ Step 4: Inserting products into database...");

  let insertedCount = 0;
  for (const product of products) {
    try {
      await prisma.product.create({
        data: {
          produto: product.produto,
          imagem: product.imagem,
          nome: product.nome,
          tamanhosDisponiveis: JSON.stringify(product.tamanhosDisponiveis),
          tipo: product.tipo,
          preco: product.preco,
          loja: product.loja,
          link: product.link,
          descricao: product.descricao,
          parteDoCorpo: product.parteDoCorpo,
          genero: product.genero,
        },
      });
      insertedCount++;
      console.log(`  ‚úì Inserted: ${product.nome}`);
    } catch (error) {
      console.error(`  ‚úó Failed to insert ${product.nome}:`, error);
    }
  }

  console.log(`\n‚úÖ Seed complete!`);
  console.log(`   - Inserted ${insertedCount} products`);
  console.log(`   - Images saved to: ${uploadsDir}`);
}

